INSERT INTO `vacancies` VALUES (1573,'Менеджер по работе с клиентами','<strong>Требования:</strong><br /><ul><li>высшее образование</li><li>коммуникативные навыки</li><li>аккуратность&sbquo; внимательность</li><li>решительность</li><li>навыки самопрезентации</li><li>знание основ финансовых рынков</li><li>знание основ трейдинга</li><li>опыт торговли на финансовых рынках (желателен) </li></ul><strong>Обязанности:</strong><br /><ul><li>консультирование клиентов по услугам компании в офисе&sbquo; по телефону и на форуме компании</li><li>оказание помощи клиентам при открытии торговых счетов</li><li>оформление клиентских документов</li><li>проведение презентаций и семинаров</li><li>контроль региональных рекламных кампаний</li><li>составление отчетности о работе филиала</li></ul><strong>Условия:</strong><br /><ul><li>работу в динамичной&sbquo; растущей компании</li><li>возможность профессионального роста и развития</li><li>обучение за счет компании </li><li>официальное трудоустройство и соц.пакет</li><li>оплата труда на испытательном сроке 800$ </li></ul>',26881924,'finance','ekb',20000,NULL,NULL,'Broco Investments Inc','2008-09-15 08:27:05','2008-09-19 21:45:45');
