INSERT INTO `vacancies` VALUES (1670,'БУХГАЛТЕР','Компании по продаже и ремонту автомобилей-иномарок требуется бухгалтер со знанием упрощенной системы налогообложения. \r\nОбязанности: ведение бухгалтерии юридического лица по упрощенной системе налогообложения, участок расчетов с поставщиками.\r\nТребования: внимательность, аккуратность, знание УСН, 1С Бухгалтерия, порядка расчетов с поставщиками\r\nУсловия: график работы: 5-ти дневная рабочая неделя, соцпакет, премии по результатам работы.',21077588,'finance','msk',30000,NULL,NULL,'РЕДЕГИ','2008-09-19 13:16:11','2008-09-19 21:45:45');
