INSERT INTO `vacancies` VALUES (1740,'Менеджер по персоналу','<strong>Требования:</strong>&nbsp; Менеджер по персоналу требуется на производство видеопродукции. <br />Жен. 23-40 л.&sbquo; в/о&sbquo; с опытом работы от 1 г.&sbquo; ПК-пользователь.<br /><ul><li>&nbsp;&nbsp;</li></ul><strong>Обязанности:</strong>&nbsp; поиск&sbquo; подбор персонала&sbquo; размещение рекламы в СМИ&sbquo; проведение собеседований&sbquo; адаптация персонала. Ведение кадрового делопроизводства: оформление приказов&sbquo; личных дел&sbquo; карточек Т-2&sbquo; журналов учета&sbquo; штатного расписания&sbquo; отпусков&sbquo; б/л.<br /><ul><li>&nbsp;&nbsp;</li></ul><strong>Условия:</strong>&nbsp; пн-пт 10-19ч.&sbquo; з/пл 23000 руб.&sbquo; ТК РФ&sbquo; отпуск&sbquo; б/л.<br />ст. М &quot;Петровско-Разумовская&quot;<br />т.482-03-55 <br />e-mail: personal@laservideo.ru<br /><ul><li>&nbsp;&nbsp;</li></ul>',25373092,'hr','msk',20000,23000,NULL,'Лазер-Видео','2008-09-19 10:00:06','2008-09-19 21:45:46');
