INSERT INTO `vacancies` VALUES (1809,'Менеджер технического отдела','Требования: Муж.&sbquo; 25-47 лет&sbquo; гражданство РФ или Белоруссия&sbquo; в/о или ср.-спец.&sbquo; опыт\r\nработы в аналогичной области желателен&sbquo; английский или французский &ndash; средний\r\nуровень&sbquo; ПК &ndash; уверенный пользователь.\r\nОбязанности: Работа с клиентами в России и с головной фирмой по координации\r\nпоставок запчастей в Россию. Реализация запчастей. Координация вопросов сервисного обслуживания&sbquo;  взаимодействие с сервисными инженерами и руководством отдела послепродажного обслуживания.\r\nЗ/п оклад 1400 &ndash; 1600$ - net + бонусы + оплата мобильной связи + компенсация проезда.\r\nГрафик работы пн.-пт. 10.00-18.00  м. Фрунзенская\r\ne-mail: resume@topskills.ru, anastasiya.silkina@topskills.ru \r\nКонсультант по подбору персонала Силкина Анастасия \r\n(ОБЯЗАТЕЛЬНО УКАЗАТЬ КОД ВАКАНСИИ ATO В ТЕМЕ)',25516714,'logistics','msk',32900,37600,NULL,'TopSkills / Personnel Consulting /','2008-09-19 12:49:16','2008-09-19 21:45:46');
