INSERT INTO `vacancies` VALUES (1909,'Ведущий аналитик-обозреватель','<strong>Требования:</strong><br />В крупную инвестиционно-промышленную группу требуется ведущий аналитик-обозреватель: муж./жен.&sbquo; от 25-35 лет&sbquo; в/о (журналистика&sbquo; экономика&sbquo; маркетинг)&sbquo; гражданство РФ&sbquo; с опытом работы от 2-х лет в информационно-аналитических отделах торгово-производственных компаний&sbquo; опытный пользователь ПК (MS Office)&sbquo; желательно знание английского языка. Способность к быстрому анализу и систематизации информации&sbquo; коммуникабельность&sbquo; активность&sbquo; ответственность.<br /><strong>Обязанности:</strong><br />информационный мониторинг&sbquo; формирование аналитических обзоров и экспертного мнения. Подготовка статистической информации по развитию лесопромышленной отрасли&sbquo; оперативные информационные сводки&sbquo; обзор текущи хновостей ЛПК&sbquo; прогнозы по развитию. <br /><strong>Условия:</strong><br />&nbsp;ст. м. Речной вокзал/ Водный стадион&sbquo; з./плата 50000 рублей&sbquo; премии&sbquo;&nbsp; оформление по ТК РФ&sbquo; соц. пакет (отпуск&sbquo; больничный)&sbquo; график работы с 9-18&sbquo;&nbsp; Пн-Пт.',24427685,'finance','msk',50000,50000,NULL,'Зест-групп','2008-09-19 06:47:28','2008-09-19 21:45:47');
