INSERT INTO `vacancies` VALUES (1911,'маркетолог-аналитик','Крупной компании по продаже книжной продукции требуется маркетолог-аналитик.<br /><strong>Требования:</strong><br />Жен.&sbquo; 25-35 лет&sbquo; в/о&sbquo; опыт работы в аналогичной должности от 1 года&sbquo; опыт проведения аналитических работ&sbquo; знание методологии проведения стат. анализа&sbquo; знание MS Excel - на уровне продвинутого пользователя. Аналитический склад ума&sbquo; внимательность&sbquo; скрупулезность<br /><strong>Обязанности:</strong><br />анализ фактических бюджетных заказов по относительным и абсолютным показателям; изучение конъюнктуры книготоргового рынка; анализ потребности в книжной продукции; написание аналитических отчетов (анализ динамики&sbquo; предложения&sbquo; выводы)<br /><strong>Условия:</strong><br />от 30 000 рублей &sbquo; льготное питание&sbquo; премии по итогам года. Оформление по ТК РФ<br />Резюме направлять на адрес hot@rezervpersonal.ru в теме письма указать код вакансии &quot;МА&quot;&nbsp;Тел. (495) 978 30 73&sbquo; (495) 783 52 13 доб. 102 Бармина Маргарита',26989874,'marketing','msk',30000,NULL,NULL,'Rezerv-Personal','2008-09-19 06:43:21','2008-09-19 21:45:47');
