INSERT INTO `vacancies` VALUES (1922,'Риэлтор, специалист по недвижимости &quot;Киевское&quot;','&laquo;Инком-недвижимость&raquo; &laquo;Киевское&raquo; приглашает на работу по специальности &quot;Риэлтор&quot; сотрудников с опытом и без опыта работы в недвижимости. <br />Мы предлагаем: бесплатное профессиональное обучение&sbquo; информационную&sbquo; рекламную и юридическую поддержку&sbquo; перспективы профессионального и карьерного роста. <br />Пожелания к кандидатам: возраст от 22 до 55 лет&sbquo; образование не ниже средне-специального. Гражданство РФ. <br />Получить более полную информацию по вакансии и записаться на собеседование Вы можете по телефону (495) 363-04-24',23563261,'realty','msk',NULL,NULL,NULL,'Корпорация &quot;ИНКОМ-недвижимость&quot;','2008-09-19 08:29:34','2008-09-19 21:45:47');
