INSERT INTO `vacancies` VALUES (1961,'Консультант по декоративной косметике','Ведущая российская компания декоративной косметики приглашает Консультантов.<br />&nbsp;<br /><strong>Обязанности:</strong><br />&nbsp;<br /><ul><li>Консультирование и обслуживание покупателей </li><li>Пополнение ассортимента на стеллажах </li><li>Контроль за соответствием ценников </li></ul>&nbsp;<br />Пожелания:<br />&nbsp;<br /><ul><li>Приятная внешность&sbquo; ухоженность&sbquo; опрятность </li><li>Правильная речь </li><li>Коммуникабельность&sbquo; общительность </li><li>Возраст от 18 лет </li><li>Без вредных привычек </li><li>Регистрация в Москве и МО&sbquo; Гражданство РФ </li><li>Опыт не обязателен </li></ul>&nbsp;<br />Мы предлагаем:<br />&nbsp;<br /><ul><li>График работы: 5/2 или 2/2 </li><li>Средняя з/п 18 000-20&nbsp;000 </li><li>Бесплатное обучение </li><li>Социальный пакет&sbquo; согласно ТК РФ </li><li>Оплата проезда </li><li>Дружный коллектив </li></ul>',26900470,'retail','msk',18000,NULL,NULL,'прямой работадатель','2008-09-19 12:52:17','2008-09-19 21:45:47');
