INSERT INTO `vacancies` VALUES (1963,'Консультант ( декоративная косметика)','Национальная Торговая Сеть Градиент - это современная дистрибьюторская компания всероссийского масштаба.<br />&nbsp;<br />Имея свои филиалы в 14 городах России&sbquo; Градиент успешно представляет на рынке как традиционный ассортимент мультинациональных и российских производителей&sbquo; так и уникальный портфель торговых марок&sbquo; делающих первые шаги на российском рынке парфюмерии и косметики. Опытная команда&sbquo; акцент на современные технологии и особая корпоративная культура сделали Градиент лидирующим дистрибьютором в своем секторе.<br />&nbsp;<br />&laquo;Градиент&raquo; приглашает активных и амбициозных на позицию Консультанта.<br />&nbsp;<br /><strong>Обязанности:</strong><br /><ul><li>Консультирование и обслуживание покупателей </li><li>Пополнение ассортимента на стеллажах </li><li>Контроль за соответствием ценников </li></ul>&nbsp;<br />Пожелания:<br /><ul><li>Приятная внешность&sbquo; ухоженность&sbquo; опрятность </li><li>Правильная речь </li><li>Коммуникабельность&sbquo; общительность </li><li>Возраст от 18 лет </li><li>Без вредных привычек </li><li>Регистрация в Москве и МО&sbquo; Гражданство РФ </li><li>Опыт не обязателен </li></ul>&nbsp;<br />Мы предлагаем:<br /><ul><li>График работы: 5/2 или 2/2 </li><li>Высокая з/п ( оклад + %) </li><li>Бесплатное обучение </li><li>Социальный пакет&sbquo; согласно ТК РФ </li><li>Оплата проезда </li><li>Дружный коллектив </li></ul>',26960847,'retail','msk',NULL,NULL,NULL,'Градиент','2008-09-19 12:51:20','2008-09-19 21:45:47');
