INSERT INTO `vacancies` VALUES (1968,'Менеджер-консультант (сменный график 2/2 м.Юго-Западная ТЦ &quot; Румянцево)','В московскую торговую компанию требуется менеджер-консультант по продажам элитной итальянской мебели.\r\nТребования: жен., 20 - 45 лет, гражданство РФ, образование средне-специальное, неоконченное высшее, высшее, рассматриваются соискатели с минимальным опытом работы продавцом, менеджером по продажам.\r\nЛичные качества: коммуникабельность,ответственность, стрессоустойчивость, адекватность, доброжелательность.\r\nОбязанности: консультирование клиентов, заключение и ведение договоров (без материальной ответственности).\r\nКонтроль за поставками продукции.\r\nУсловия: м. Юго-Западная ТЦ &quot; Румянцево) пн/пт, график работы сменный: 2/2 (с 10.00. до 21.00.), з/п оклад+ % от продаж, трудоустройство по ТК.',26388433,'retail','msk',20000,30000,NULL,'Торговая компания','2008-09-19 12:47:51','2008-09-19 21:45:47');
