INSERT INTO `vacancies` VALUES (1970,'Продавец-консультант','Требования: Муж./жен., 22-45, гражданство РФ\r\nили Белоруссия, ср.-спец. или в/о, ПК-\r\nуверенный пользователь, опыт работы\r\nжелателен.\r\nОбязанности: Консультирование клиентов\r\nпо ассортименту товаров, помощь в выборе\r\nтовара, презентация товаров, участие в\r\nвыставках.\r\nЗ/п оклад от 25 000 руб. + % процент с продаж + \r\nперспектива карьерного роста. График\r\nработы: пн.-пт. 9.00-18.00  м. Сходненская\r\ne-mail: resume@topskills.ru, kotova@topskills.ru Менеджер по\r\nподбору персонала Котова Елена\r\n(ОБЯЗАТЕЛЬНО УКАЗАТЬ КОД ВАКАНСИИ PPK  В\r\nТЕМЕ), Тел.: (495) 649-46-75.',26327074,'retail','msk',25000,35000,NULL,'TopSkills / Personnel Consulting /','2008-09-19 12:47:27','2008-09-19 21:45:47');
