INSERT INTO `vacancies` VALUES (1976,'Продавец','Продавец в булочную-кондитерскую премиум-класса (м. Кунцевская)<br />&nbsp;<br />Нам нужны люди:<br /><ul><li>с позитивным взглядом на жизнь</li><li>умеющие работать в команде</li><li>коммуникабельные</li><li>работающие на результат и умеющие его добиваться</li></ul><strong>Обязанности:</strong><br /><ul><li>продажа теплого&sbquo;свежевыпеченного хлеба и дружеское общение с покупателями</li></ul><strong>Условия:</strong><br /><ul><li>работа в дружном и веселом коллективе с большой перспективой роста</li><li>оформление по ТК РФ&sbquo; оплачиваемый отпуск и больничный лист</li></ul>Если Вы любите людей&sbquo; в работе для Вас важно получение позитивных эмоций&sbquo; отличный коллектив и честное отношение руководства - тогда добро пожаловать к нам!',26837474,'restaurant','msk',20000,30000,NULL,'Буланжери','2008-09-19 12:42:32','2008-09-19 21:45:47');
