INSERT INTO `vacancies` VALUES (2461,'Менеджер по продажам.','Компания: &quot;Нью-Эдженси&quot; Высшее (законченное), основное или дополнительное образование в области архитектуры или строительства будет преимуществом. Опыт работы в сфере продаж не менее 1,5 лет. Желателен опыт работы в сфере продаж товаров класса премиум (группа люкс) или мебели не менее 1 года в должности менеджера по продажам/ менеджера-консультанта. Обязанности: Прямые продажи: работа с клиентом, грамотное и оперативное оформление заказа в установленной форме, планирование и контроль доставки и установки товара от склада до клиента, выписка счета. Обеспечение информационной поддержки архитекторов. Контроль возврата дебиторской задолженности. Контакты: , Тел. 222-65-04, 222-77-08 Email: info@new-agency.ru',27008084,'retail','nsk',NULL,NULL,NULL,NULL,'2008-09-16 03:16:40','2008-09-19 21:45:50');
