INSERT INTO `vacancies` VALUES (2573,'Бухгалтер на участок','Приглашаем принять участие в конкурсе на замещение должности бухгалтера на участок. Требования: опыт работы не менее 1 года на участке: зарплата, Банк, касса. Отличное знание 1С: Предприятие 7.7, Зарплата и Кадры, внимательность, грамотность, аккуратность, ответственность, отсутствие вредных привычек. Женщина, 21-45 лет, высшее/неполное высшее образование. Обязанности: на участок учет зарплаты, отчеты ЕСН, соц. страх., НДФЛ, Банк, касса, кадровый учет (трудовые договора и трудовые книжки). Условия: работа в офисе (БЦ &quot;Кондратьевский&quot;), 5 минут от метро Пл. Ленина, Кондратьевский пр., д. 15, корп. 3, понедельник - пятница с 10.00 до 18.00, дружный коллектив, хороший офис, о оформление по трудовой, полный рабочий день. Предварительная запись на собеседование с 11.00 до 17.00',27043214,'finance','spb',25000,25000,NULL,'Строительная организация','2008-09-19 10:08:53','2008-09-19 21:45:50');
