INSERT INTO `vacancies` VALUES (2746,'Провизор фармацевт','<strong>Требования:</strong><br />-умение быстро осваивать новые методы работы&sbquo; высокая обучаемость;<br />-обязательность при работе с персоналом<br />-отличные коммуникативные навыки&sbquo; навыки делового общения; <br />-проявление активности; <br /><strong>Обязанности:</strong><br />-консультирование по продукции&sbquo;<br />-специальная подготовка и повышение квалификации работников всех звеньев по профилирующим специальностям&sbquo;<br />-разработка учебных курсов&sbquo; проведение учебных занятий.<br /><strong>Условия:</strong><br />-работа в успешной и быстро развивающейся компании; <br />-работа в офисе <br />-бесплатное обучения в процессе работы&sbquo; <br />-карьерный рост; <br />-гибкий график работы <br />-возможность раскрытия творческого потенциала;',26418433,'medicine','spb',15000,NULL,NULL,'ип','2008-09-18 03:48:22','2008-09-19 21:45:52');
