INSERT INTO `vacancies` VALUES (2851,'Приемщица','Крупная производственная компания, ЗАО &quot;Вторчермет&quot; (Холдинг СеверСталь), открывает вакансию приемщица, по м/лому, женщина, от 22 до 50 лет, образование среднее, среднее специальное, знание ПК - желательно, гражданство РФ, регистрация в Санкт-Петербурге или Ленинградской области. График работы сменный, 1/3. Место работы: район м. Балтийская, Морской порт. Мы предлагаем: стабильную работу и своевременную выплату заработной платы два раза в месяц. Оформление в соответствии с трудовым кодексом РФ, полный социальный пакет, работу в дружном коллективе профессионалов. За дополнительной информацией обращаться по телефону, либо по адресу: Химический пер., 4, в отдел кадров, с понедельника по пятницу, с 9.00 до 12.00',27044301,'service','spb',17000,17000,NULL,'Производственная компания, ЗАО &quot;Вторчермет&quot; (Холдинг СеверСталь)','2008-09-19 10:33:47','2008-09-19 21:45:52');
INSERT INTO `vacancies` VALUES (2852,'Грузчик','Фирме, занимающаяся оптовой продажей пива и других напитков, требуются на постоянную работу грузчиков. Опыт работы на погрузчике поощряется. Возраст до 35 лет. График работы пятидневка + две субботы в месяц, воскресенье - выходной. Прописка обязательна. Знание русского языка. Трудоустройство по трудовому кодексу РФ. Адреса складов: Коломяжский пр., 13, склад &#8470; 8. Звоните: 394-94-66, 393-78-00, Бельцов Юрий',27044292,'service','spb',20000,20000,NULL,'Фирма, занимающаяся оптовой продажей пива и других напитков','2008-09-19 10:33:45','2008-09-19 21:45:52');
