INSERT INTO `vacancies` VALUES (2915,'Менеджер по продажам','Торгово-производственная итальянская компания приглашает менеджера по продажам.\r\nТребования: М/Ж&sbquo; 25-35 лет&sbquo; высшее образование&sbquo; знание итальянского языка&sbquo; опыт работы от 1 года.\r\nОбязанности: Поиск клиентов&sbquo; проведение переговоров&sbquo; работа с договорами&sbquo; сопровождение сделки.\r\nУсловия: График пятидневка с 9.00 &ndash; 18.00&sbquo; официальное трудоустройство&sbquo; заработная плата не ниже 55000 руб.',26513901,'wholesale','spb',55000,60000,NULL,'Кадровое агентство &quot;StaffRegion&quot;','2008-09-19 10:58:03','2008-09-19 21:45:53');
